import React, { useEffect, useState } from "react";
import "./Email.css";
import axios from "axios";
import { API_BASE_URL, ACCESS_TOKEN_NAME } from "../../constants/apiConstants";
import { withRouter } from "react-router-dom";

function Email(props) {
  const [state, setDetails] = useState({
    confrimPassword: "",
    newEmail: "",
    oldEmail: "",
  });

  const handleChange = (e) => {
    const { id, value } = e.target;
    setState((prevState) => ({
      ...prevState,
      [id]: value,
    }));
  };
  const handleSubmitClick = (e) => {
    e.preventDefault();
    const payload = {
      //needs to be changed to email maybe.
      newEmail: state.newEmail,
      oldEmail: state.oldEmail,
      confirmPassword: state.confirmPassword,
    };
    axios
      .post(API_BASE_URL + "/api/login", payload)
      .then(function (response) {
        if (response.status === 200) {
          setState((prevState) => ({
            ...prevState,
            successMessage: "Update Complete. Redirecting to settings..",
          }));
          localStorage.setItem(ACCESS_TOKEN_NAME, response.data.token);
          redirectToHome();
          props.showError(null);
        } else if (response.code === 204) {
          props.showError("Password Incorrect");
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  return (
    <div className="grid-container">
      <div className="grid-item">
        <div class="grid-item">
          <form onSubmit={submitHandler}>
            <div className="form-inner">
              <h2>Update Email</h2>
              <div class="form-group">
                <label htmlFor="newEmail">New Email</label>
                <input
                  type="text"
                  name="newEmail"
                  id="newEmail"
                  value={state.newEmail}
                  onChange={handleChange}
                />
              </div>
              <div class="form-group">
                <label htmlFor="oldEmail">Old Email</label>
                <input type="text" name="oldEmail" id="oldEmail" />
              </div>
              <h3>To verify your identity, please enter your password</h3>
              <div class="form-group">
                <label htmlFor="password">Password</label>
                <input type="password" name="password" id="password" />
              </div>
              <input type="submit" value="Save Changes" />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Email;
